mapper.select:
SELECT cart_id, session_id, menu_qty, date_format(c.reg_date, '%Y-%m-%d') as r_date, date_format(c.edit_date, '%Y-%m-%d') as e_date, member_id, menu_id, is_direct_order ,CONVERT(list_img USING utf8) as list_img , name, price FROM cart c INNER JOIN menu m ON c.menu_id = m.id
member_id=#{member_id}
session_id=#{session_id}
AND is_direct_order = "N"
ORDER BY cart_id DESC

mapper.insert:
INSERT INTO cart (
session_id, menu_qty, reg_date, edit_date, member_id, menu_id, is_direct_order
) VALUES (
#{session_id}, #{menu_qty}, now(), now(), #{member_id}, #{menu_id}, #{is_direct_order}
)

mapper.delete:
		DELETE FROM cart 




                    member_id=#{member_id}


                    session_id=#{session_id}



            AND cart_id = #{cart_id}

mapper.select:
SELECT COUNT(*) FROM cart


		session_id = #{session_id}


		AND member_id = #{member_id}


		AND is_direct_order = #{is_direct_order}


		AND reg_date = #{reg_date}


		AND edit_date = #{edit_date}
