mapper.update:
UPDATE gift SET
member_id=null
WHERE member_id=#{member_id}

mapper.select:
SELECT gift_id, member_id, to_mem_email, to_mem_name, message, gift_price, pay_method, date_format(reg_date, '%Y-%m-%d') as r_date FROM gift
member_id=#{member_id}
AND date_format(reg_date, '%Y%m%d') BETWEEN date_format(date_add(now(), INTERVAL #{pickPeriod} MONTH), '%Y%m%d') AND date_format(now(), '%Y%m%d')
ORDER BY gift_id DESC
LIMIT #{offset}, #{listCount}

mapper.insert:
INSERT INTO gift (
member_id, to_mem_email, to_mem_name, message, gift_price, pay_method, reg_date
) VALUES (
#{member_id}, #{to_mem_email}, #{to_mem_name}, #{message}, #{gift_price}, "N", now()
)

mapper.select:
SELECT COUNT(*) FROM gift
member_id=#{member_id}
AND date_format(reg_date, '%Y%m%d') BETWEEN date_format(date_add(now(), INTERVAL #{pickPeriod} MONTH), '%Y%m%d') AND date_format(now(), '%Y%m%d')
